package com.unn.engine;import com.unn.engine.data.Datasets;import com.unn.engine.dataset.OuterDataset;import com.unn.engine.metadata.*;import com.unn.engine.mining.splitters.SimpleSplitter;import com.unn.engine.utils.Pair;import org.junit.Test;import java.util.ArrayList;import static org.junit.Assert.*;public class TestSplitters {    @Test    public void testSimpleSplitter() {        ArrayList<Integer> times = new ArrayList<>();        times.add(101);        times.add(102);        times.add(103);        times.add(1);        times.add(3);        times.add(5);        times.add(2);        times.add(4);        for (int i = 6; i < 100; ++i) {            times.add(i);        }        SimpleSplitter splitter = new SimpleSplitter(0.75);        Pair<ArrayList<Integer>, ArrayList<Integer>> split = splitter.split(times);        assertFalse(split.first().contains(101));        assertFalse(split.first().contains(102));        assertFalse(split.first().contains(103));        assertTrue(split.first().contains(1));        assertFalse(split.first().contains(95));        assertFalse(split.second().contains(1));        assertTrue(split.second().contains(90));    }}